public void AddColumn(string name, DataTypes type)

public void DeleteRecords(List<object[]> recds)
{
    foreach (object[] r in recds)
    {
        if (!this.records.Contains(r))
            throw new Exception("Record + " + r.ToString() + " not in the table");
        records.Remove(r);
    }
}

